import styled from "styled-components";

export const Container = styled.ul`

    /* display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    align-items: center;
    width: 45vh; */

    overflow-x: scroll;
    top: -16px;
    position: absolute;
    padding: 0px;
    width: 100%;
    left: 0px;
    height: 75%;

    li{
        display: flex;
        list-style: none;
        align-items: center;
        justify-content: space-around;
        margin-bottom: 5px;
        margin-top: 5px;
        width: 95%;
        margin-left: 5px;
    }


`
