import { addToCart } from "../../store/modules/cart/actions"

export const addToCartMiddler = (product) => {

    return (dispatch) =>{

        const list = JSON.parse(localStorage.getItem('')) || []
        list.push(product)
        localStorage.setItem('cart', JSON.stringify(list))

        dispatch(addToCart(product))
    }
}
