import { addToCart, removeFromCart } from "./actions"

export const addToCartMiddler = (product, isRemovable = true) => {

    return (dispatch) => {

      const list = JSON.parse(localStorage.getItem("cart")) || []

      list.push(product)

      localStorage.setItem("cart", JSON.stringify(list))

      isRemovable = false

      dispatch(addToCart(product))

    }
  }

  export const removeFromCartMiddler = (id) => (dispatch, getStore) => {

    const { cart } = getStore()

    const list = cart.filter((product) => product.id !== id)

    localStorage.setItem("cart", JSON.stringify(list))

    dispatch(removeFromCart(list))

  }

  export const handleClick = (productId) => {

    const findIndex = currentSale.findIndex((item) => productId === item.id)

    const filter = products.filter((item) => productId === item.id)

    if(findIndex === -1){

      setCurrentSale([...currentSale, ...filter])
    }
  }
