const cartReducer = (state = JSON.parse(localStorage.getItem("cart")) || [], action) => {


  const findIndex = state.findIndex((item) => produc.id === item.id)

  const filter = state.filter((item) => productId === item.id)

    switch (action.type) {

      case "@cart/ADD" && findIndex === -1:

        const { product } = action

        return [...state, product]

      case "@cart/REMOVE":

        const { list } = action

        return list

      default:
        return state
  }
}

export default cartReducer
