const cartReducer = (state = JSON.parse(localStorage.getItem("cart")) || [], action) => {

  const handleClick = (productId) => {

    const findIndex = state.findIndex((item) => productId === item.id)

    const filter = state.filter((item) => productId === item.id)

    if(findIndex === -1){

      setCurrentSale([...filter])
    }
  }

    switch (action.type) {

      case "@cart/ADD" && 'findIndex === -1':

        const { product } = action

        return [...state, product]

      case "@cart/REMOVE":
        const { list } = action
        return list

      default:
        return state
  }
}

export default cartReducer
