const cartReducer = (state = JSON.parse(localStorage.getItem("cart")) || [], action) => {

    switch (action.type) {

      case "@cart/ADD":

        const { product } = action

        const findIndex = product.findIndex((item) => productId === item.id)

        if(findIndex === -1){

          return [...state, product]
        }

      case "@cart/REMOVE":
        const { list } = action
        return list

      default:
        return state
  }
}

export default cartReducer
